(function() {
    var config = window.embeddedComponentConfig || {};
    var componentId = config.componentId || 'defaultComponentId';
    var domain = config.domain || 'defaultDomain';

    var script = document.createElement('script');
    script.src = 'http://' + domain + '/scripts/embedded-component.js';
    script.onload = function() {
        if (typeof window.renderEmbeddedComponent === 'function') {
            window.renderEmbeddedComponent(config);
        }
    };
    document.body.appendChild(script);
})();
